import React, { useState, useEffect } from 'react';
import { getRestaurantImage, validateImageUrl } from '../utils/imageUtils';

const RestaurantImage = ({ restaurant, className }) => {
  const [imageError, setImageError] = useState(false);

  // Get the appropriate image source
  const getImageSource = () => {
    if (imageError || !restaurant.images || restaurant.images.length === 0) {
      return getRestaurantImage(restaurant.cuisine, restaurant._id);
    }
    return validateImageUrl(restaurant.images[0]);
  };

  return (
    <img
      src={getImageSource()}
      alt={restaurant.name}
      className={className || ''}
      onError={() => setImageError(true)}
      loading="lazy"
    />
  );
};

export default RestaurantImage;
