import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { FaMapMarkerAlt, FaMotorcycle, FaStore, FaCheckCircle, FaPhoneAlt } from 'react-icons/fa';
import './OrderTracking.css';

const OrderTracking = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { token } = useAuth();

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`/api/orders/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrder(res.data.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch order details');
        setLoading(false);
      }
    };

    fetchOrder();
  }, [id, token]);

  if (loading) {
    return <div className="loading">Loading order details...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  // For demo purposes, create a mock order if none exists
  const demoOrder = order || {
    _id: 'order123',
    restaurant: {
      _id: 'rest1',
      name: 'Burger Joint',
      address: {
        street: '123 Main St',
        city: 'Anytown',
        state: 'CA',
        zipCode: '12345',
      },
      phone: '(555) 123-4567',
    },
    items: [
      {
        name: 'Cheeseburger',
        quantity: 2,
        price: 8.99,
      },
      {
        name: 'French Fries',
        quantity: 1,
        price: 3.99,
      },
    ],
    totalAmount: 21.97,
    deliveryFee: 2.99,
    tax: 1.76,
    status: 'out_for_delivery',
    estimatedDeliveryTime: new Date(Date.now() + 1000 * 60 * 15).toISOString(), // 15 minutes from now
    createdAt: new Date().toISOString(),
    deliveryAddress: {
      street: '456 Oak Ave',
      city: 'Anytown',
      state: 'CA',
      zipCode: '12345',
    },
    driver: {
      name: 'John Driver',
      phone: '(555) 987-6543',
    },
    trackingInfo: {
      currentLocation: {
        coordinates: [-122.4194, 37.7749], // Example coordinates
      },
      status: 'On the way',
      updatedAt: new Date().toISOString(),
    },
  };

  // Helper function to format date
  const formatTime = (dateString) => {
    const options = { 
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleTimeString(undefined, options);
  };

  // Helper function to get status step
  const getStatusStep = (status) => {
    switch (status) {
      case 'pending':
        return 0;
      case 'confirmed':
        return 1;
      case 'preparing':
        return 2;
      case 'out_for_delivery':
        return 3;
      case 'delivered':
        return 4;
      default:
        return 0;
    }
  };

  const currentStep = getStatusStep(demoOrder.status);

  return (
    <div className="order-tracking-page">
      <div className="tracking-header">
        <h1>Order Tracking</h1>
        <p className="order-id">Order #{demoOrder._id.slice(-6)}</p>
      </div>

      <div className="tracking-container">
        <div className="tracking-status">
          <div className="status-timeline">
            <div className={`status-step ${currentStep >= 0 ? 'active' : ''}`}>
              <div className="step-icon">
                <FaCheckCircle />
              </div>
              <div className="step-content">
                <h3>Order Placed</h3>
                <p>{formatTime(demoOrder.createdAt)}</p>
              </div>
            </div>

            <div className={`status-step ${currentStep >= 1 ? 'active' : ''}`}>
              <div className="step-icon">
                <FaCheckCircle />
              </div>
              <div className="step-content">
                <h3>Order Confirmed</h3>
                <p>{currentStep >= 1 ? formatTime(demoOrder.createdAt) : 'Pending'}</p>
              </div>
            </div>

            <div className={`status-step ${currentStep >= 2 ? 'active' : ''}`}>
              <div className="step-icon">
                <FaStore />
              </div>
              <div className="step-content">
                <h3>Preparing</h3>
                <p>{currentStep >= 2 ? formatTime(demoOrder.createdAt) : 'Pending'}</p>
              </div>
            </div>

            <div className={`status-step ${currentStep >= 3 ? 'active' : ''}`}>
              <div className="step-icon">
                <FaMotorcycle />
              </div>
              <div className="step-content">
                <h3>Out for Delivery</h3>
                <p>{currentStep >= 3 ? formatTime(demoOrder.createdAt) : 'Pending'}</p>
              </div>
            </div>

            <div className={`status-step ${currentStep >= 4 ? 'active' : ''}`}>
              <div className="step-icon">
                <FaMapMarkerAlt />
              </div>
              <div className="step-content">
                <h3>Delivered</h3>
                <p>{currentStep >= 4 ? formatTime(demoOrder.createdAt) : 'Estimated: ' + formatTime(demoOrder.estimatedDeliveryTime)}</p>
              </div>
            </div>
          </div>
        </div>

        <div className="tracking-details">
          <div className="tracking-section">
            <h2>Delivery Details</h2>
            <div className="delivery-address">
              <FaMapMarkerAlt className="icon" />
              <div>
                <h3>Delivery Address</h3>
                <p>
                  {demoOrder.deliveryAddress.street}, {demoOrder.deliveryAddress.city}, {demoOrder.deliveryAddress.state} {demoOrder.deliveryAddress.zipCode}
                </p>
              </div>
            </div>

            {demoOrder.driver && currentStep >= 3 && (
              <div className="driver-info">
                <FaMotorcycle className="icon" />
                <div>
                  <h3>Delivery Driver</h3>
                  <p>{demoOrder.driver.name}</p>
                  <a href={`tel:${demoOrder.driver.phone}`} className="driver-phone">
                    <FaPhoneAlt /> {demoOrder.driver.phone}
                  </a>
                </div>
              </div>
            )}
          </div>

          <div className="tracking-section">
            <h2>Order Summary</h2>
            <div className="restaurant-info">
              <h3>{demoOrder.restaurant.name}</h3>
              <p>
                {demoOrder.restaurant.address.street}, {demoOrder.restaurant.address.city}
              </p>
              <a href={`tel:${demoOrder.restaurant.phone}`} className="restaurant-phone">
                <FaPhoneAlt /> {demoOrder.restaurant.phone}
              </a>
            </div>

            <div className="order-items">
              {demoOrder.items.map((item, index) => (
                <div key={index} className="order-item">
                  <div className="item-info">
                    <span className="item-quantity">{item.quantity}x</span>
                    <span className="item-name">{item.name}</span>
                  </div>
                  <div className="item-price">${(item.price * item.quantity).toFixed(2)}</div>
                </div>
              ))}
            </div>

            <div className="price-summary">
              <div className="price-row">
                <span>Subtotal</span>
                <span>${(demoOrder.totalAmount - demoOrder.deliveryFee - demoOrder.tax).toFixed(2)}</span>
              </div>
              <div className="price-row">
                <span>Delivery Fee</span>
                <span>${demoOrder.deliveryFee.toFixed(2)}</span>
              </div>
              <div className="price-row">
                <span>Tax</span>
                <span>${demoOrder.tax.toFixed(2)}</span>
              </div>
              <div className="price-row total">
                <span>Total</span>
                <span>${demoOrder.totalAmount.toFixed(2)}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="tracking-actions">
        <Link to="/orders" className="btn btn-secondary">
          Back to Orders
        </Link>
        <Link to={`/restaurants/${demoOrder.restaurant._id}`} className="btn">
          Reorder
        </Link>
      </div>
    </div>
  );
};

export default OrderTracking;
