import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Orders.css';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { token, isAuthenticated } = useAuth();

  useEffect(() => {
    // Use mock data directly instead of API call
    const mockOrders = [
      {
        _id: 'order1',
        restaurant: {
          _id: 'rest1',
          name: 'Tiamo',
        },
        items: [
          {
            name: 'Margherita Pizza',
            quantity: 1,
            price: 12.99,
          },
          {
            name: 'Garlic Bread',
            quantity: 1,
            price: 4.99,
          },
        ],
        totalAmount: 19.98,
        status: 'delivered',
        createdAt: new Date(Date.now() - 86400000).toISOString(), // 1 day ago
      },
      {
        _id: 'order2',
        restaurant: {
          _id: 'rest2',
          name: 'Shri Sagar CTR',
        },
        items: [
          {
            name: 'Masala Dosa',
            quantity: 2,
            price: 8.99,
          },
          {
            name: 'Filter Coffee',
            quantity: 1,
            price: 3.99,
          },
        ],
        totalAmount: 21.97,
        status: 'out_for_delivery',
        createdAt: new Date().toISOString(), // Today
      },
    ];

    setOrders(mockOrders);
    setLoading(false);
  }, []);

  // Helper function to format date
  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Helper function to get status badge class
  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending':
        return 'status-pending';
      case 'confirmed':
        return 'status-confirmed';
      case 'preparing':
        return 'status-preparing';
      case 'out_for_delivery':
        return 'status-out-for-delivery';
      case 'delivered':
        return 'status-delivered';
      case 'cancelled':
        return 'status-cancelled';
      default:
        return '';
    }
  };

  if (loading) {
    return <div className="loading">Loading orders...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="orders-page">
      <h1>My Orders</h1>

      {orders.length === 0 ? (
        <div className="no-orders">
          <p>You haven't placed any orders yet.</p>
          <Link to="/" className="btn">Browse Restaurants</Link>
        </div>
      ) : (
        <div className="orders-list">
          {orders.map((order) => (
            <div key={order._id} className="order-card">
              <div className="order-header">
                <div className="order-restaurant">
                  <h3>{order.restaurant.name}</h3>
                  <p className="order-date">{formatDate(order.createdAt)}</p>
                </div>
                <div className="order-status">
                  <span className={`status-badge ${getStatusBadgeClass(order.status)}`}>
                    {order.status.replace('_', ' ').toUpperCase()}
                  </span>
                </div>
              </div>

              <div className="order-items">
                {order.items.map((item, index) => (
                  <div key={index} className="order-item">
                    <div className="item-info">
                      <span className="item-quantity">{item.quantity}x</span>
                      <span className="item-name">{item.name}</span>
                    </div>
                    <div className="item-price">${(item.price * item.quantity).toFixed(2)}</div>
                  </div>
                ))}
              </div>

              <div className="order-footer">
                <div className="order-total">
                  <span>Total:</span>
                  <span className="total-amount">${order.totalAmount.toFixed(2)}</span>
                </div>

                <div className="order-actions">
                  <Link to={`/orders/${order._id}/tracking`} className="btn btn-secondary">
                    Track Order
                  </Link>
                  <Link to={`/restaurants/${order.restaurant._id}`} className="btn">
                    Reorder
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Orders;
