import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaSearch, FaStar, FaFilter } from 'react-icons/fa';
import './Home.css';

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    cuisine: '',
    priceRange: '',
    rating: '',
  });
  const [showFilters, setShowFilters] = useState(false);

  // Mock restaurant data for demonstration
  const mockRestaurants = [
    {
      _id: 'rest1',
      name: 'Pizza Palace',
      cuisine: ['Italian', 'American'],
      priceRange: '$$',
      rating: 4.5,
      numReviews: 120,
      images: ['https://images.unsplash.com/photo-1513104890138-7c749659a591?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80']
    },
    {
      _id: 'rest2',
      name: 'Burger Joint',
      cuisine: ['American', 'Fast Food'],
      priceRange: '$',
      rating: 4.2,
      numReviews: 85,
      images: ['https://images.unsplash.com/photo-1568901346375-23c9450c58cd?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80']
    },
    {
      _id: 'rest3',
      name: 'Sushi Express',
      cuisine: ['Japanese', 'Asian'],
      priceRange: '$$$',
      rating: 4.7,
      numReviews: 200,
      images: ['https://images.unsplash.com/photo-1579871494447-9811cf80d66c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80']
    },
    {
      _id: 'rest4',
      name: 'Taco Town',
      cuisine: ['Mexican', 'Latin American'],
      priceRange: '$$',
      rating: 4.0,
      numReviews: 150,
      images: ['https://images.unsplash.com/photo-1565299585323-38d6b0865b47?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80']
    },
    {
      _id: 'rest5',
      name: 'Curry House',
      cuisine: ['Indian', 'Asian'],
      priceRange: '$$',
      rating: 4.6,
      numReviews: 180,
      images: ['https://images.unsplash.com/photo-1585937421612-70a008356cf4?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80']
    },
    {
      _id: 'rest6',
      name: 'Noodle Bar',
      cuisine: ['Chinese', 'Asian'],
      priceRange: '$$',
      rating: 4.3,
      numReviews: 95,
      images: ['https://images.unsplash.com/photo-1569718212165-3a8278d5f624?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80']
    }
  ];

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        setLoading(true);
        // Try to fetch from API
        const res = await axios.get('/api/restaurants', {
          params: {
            search: searchTerm,
            ...filters,
          },
        });

        // If API returns data, use it
        if (res.data && res.data.data && res.data.data.length > 0) {
          setRestaurants(res.data.data);
        } else {
          // Otherwise filter mock data based on search and filters
          let filteredMockData = [...mockRestaurants];

          // Apply search term filter
          if (searchTerm) {
            const term = searchTerm.toLowerCase();
            filteredMockData = filteredMockData.filter(restaurant => 
              restaurant.name.toLowerCase().includes(term) || 
              restaurant.cuisine.some(c => c.toLowerCase().includes(term))
            );
          }

          // Apply cuisine filter
          if (filters.cuisine) {
            filteredMockData = filteredMockData.filter(restaurant => 
              restaurant.cuisine.includes(filters.cuisine)
            );
          }

          // Apply price range filter
          if (filters.priceRange) {
            filteredMockData = filteredMockData.filter(restaurant => 
              restaurant.priceRange === filters.priceRange
            );
          }

          // Apply rating filter
          if (filters.rating) {
            const minRating = parseInt(filters.rating);
            filteredMockData = filteredMockData.filter(restaurant => 
              restaurant.rating >= minRating
            );
          }

          setRestaurants(filteredMockData);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching restaurants:', err);
        // On error, use filtered mock data
        let filteredMockData = [...mockRestaurants];

        // Apply the same filters as above
        if (searchTerm) {
          const term = searchTerm.toLowerCase();
          filteredMockData = filteredMockData.filter(restaurant => 
            restaurant.name.toLowerCase().includes(term) || 
            restaurant.cuisine.some(c => c.toLowerCase().includes(term))
          );
        }

        if (filters.cuisine) {
          filteredMockData = filteredMockData.filter(restaurant => 
            restaurant.cuisine.includes(filters.cuisine)
          );
        }

        if (filters.priceRange) {
          filteredMockData = filteredMockData.filter(restaurant => 
            restaurant.priceRange === filters.priceRange
          );
        }

        if (filters.rating) {
          const minRating = parseInt(filters.rating);
          filteredMockData = filteredMockData.filter(restaurant => 
            restaurant.rating >= minRating
          );
        }

        setRestaurants(filteredMockData);
        setLoading(false);
      }
    };

    // Use a timeout to debounce the API call
    const timeoutId = setTimeout(() => {
      fetchRestaurants();
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [searchTerm, filters]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const toggleFilters = () => {
    setShowFilters(!showFilters);
  };

  const resetFilters = () => {
    setFilters({
      cuisine: '',
      priceRange: '',
      rating: '',
    });
  };

  // Mock data for demonstration
  const cuisines = ['Italian', 'Chinese', 'Indian', 'Mexican', 'Japanese', 'Thai', 'American'];
  const priceRanges = ['$', '$$', '$$$', '$$$$'];

  if (loading) {
    return <div className="loading">Loading restaurants...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="home-page">
      <section className="hero">
        <div className="hero-content">
          <h1>Delicious Food Delivered To Your Door</h1>
          <p>Order from your favorite restaurants and enjoy at home</p>
          <div className="search-container">
            <div className="search-input">
              <FaSearch className="search-icon" />
              <input
                type="text"
                placeholder="Search for restaurants or cuisines..."
                value={searchTerm}
                onChange={handleSearchChange}
              />
            </div>
            <button className="filter-button" onClick={toggleFilters}>
              <FaFilter /> Filters
            </button>
          </div>

          {showFilters && (
            <div className="filters">
              <div className="filter-group">
                <label>Cuisine</label>
                <select
                  name="cuisine"
                  value={filters.cuisine}
                  onChange={handleFilterChange}
                >
                  <option value="">All Cuisines</option>
                  {cuisines.map((cuisine) => (
                    <option key={cuisine} value={cuisine}>
                      {cuisine}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label>Price Range</label>
                <select
                  name="priceRange"
                  value={filters.priceRange}
                  onChange={handleFilterChange}
                >
                  <option value="">All Prices</option>
                  {priceRanges.map((price) => (
                    <option key={price} value={price}>
                      {price}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label>Rating</label>
                <select
                  name="rating"
                  value={filters.rating}
                  onChange={handleFilterChange}
                >
                  <option value="">All Ratings</option>
                  <option value="4">4+ Stars</option>
                  <option value="3">3+ Stars</option>
                  <option value="2">2+ Stars</option>
                </select>
              </div>

              <button className="reset-button" onClick={resetFilters}>
                Reset Filters
              </button>
            </div>
          )}
        </div>
      </section>

      <section className="restaurant-list">
        <h2>Popular Restaurants</h2>
        {restaurants.length === 0 ? (
          <p className="no-results">No restaurants found. Try different filters.</p>
        ) : (
          <div className="restaurant-grid">
            {restaurants.map((restaurant) => (
              <Link
                to={`/restaurants/${restaurant._id}`}
                className="restaurant-card"
                key={restaurant._id}
              >
                <div className="restaurant-image">
                  <img
                    src={restaurant.images[0] || 'https://via.placeholder.com/300x200?text=Restaurant'}
                    alt={restaurant.name}
                  />
                </div>
                <div className="restaurant-info">
                  <h3>{restaurant.name}</h3>
                  <div className="restaurant-meta">
                    <span className="cuisine">{restaurant.cuisine.join(', ')}</span>
                    <span className="price">{restaurant.priceRange}</span>
                  </div>
                  <div className="restaurant-rating">
                    <FaStar className="star-icon" />
                    <span>{restaurant.rating.toFixed(1)}</span>
                    <span className="review-count">({restaurant.numReviews} reviews)</span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Home;
