        // For development, use mock data
        let filteredRestaurants = [...mockRestaurants];

        // Apply search filter
        if (searchTerm) {
          const term = searchTerm.toLowerCase();
          filteredRestaurants = filteredRestaurants.filter(
            (restaurant) =>
              restaurant.name.toLowerCase().includes(term) ||
              restaurant.cuisine.some((c) => c.toLowerCase().includes(term))
          );
        }

        // Apply cuisine filter
        if (filters.cuisine) {
          filteredRestaurants = filteredRestaurants.filter((restaurant) =>
            restaurant.cuisine.includes(filters.cuisine)
          );
        }

        // Apply price range filter
        if (filters.priceRange) {
          filteredRestaurants = filteredRestaurants.filter(
            (restaurant) => restaurant.priceRange === filters.priceRange
          );
        }

        // Apply rating filter
        if (filters.rating) {
          filteredRestaurants = filteredRestaurants.filter(
            (restaurant) => restaurant.rating >= parseFloat(filters.rating)
          );
        }

        setRestaurants(filteredRestaurants);
        setLoading(false);
