import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { FaStar, FaRegStar, FaClock, FaPhone, FaGlobe, FaMapMarkerAlt } from 'react-icons/fa';
import { useCart } from '../context/CartContext';
import { toast } from 'react-toastify';
import './RestaurantDetails.css';

const RestaurantDetails = () => {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeCategory, setActiveCategory] = useState('');
  const { addToCart, cart } = useCart();

  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        setLoading(true);
        const res = await axios.get(`/api/restaurants/${id}`);
        setRestaurant(res.data.data);

        // Set first category as active if menus exist
        if (res.data.data.menus && res.data.data.menus.length > 0 && 
            res.data.data.menus[0].categories && res.data.data.menus[0].categories.length > 0) {
          setActiveCategory(res.data.data.menus[0].categories[0]);
        }

        setLoading(false);
      } catch (err) {
        setError('Failed to fetch restaurant details');
        setLoading(false);
      }
    };

    fetchRestaurant();
  }, [id]);

  const handleAddToCart = (item) => {
    if (cart.restaurant && cart.restaurant._id !== restaurant._id) {
      if (!window.confirm('Adding items from a different restaurant will clear your current cart. Continue?')) {
        return;
      }
    }

    addToCart(item, restaurant);
    toast.success(`${item.name} added to cart!`);
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      if (i <= rating) {
        stars.push(<FaStar key={i} className="star-filled" />);
      } else {
        stars.push(<FaRegStar key={i} className="star-empty" />);
      }
    }
    return stars;
  };

  if (loading) {
    return <div className="loading">Loading restaurant details...</div>;
  }

  if (error || !restaurant) {
    return <div className="error">{error || 'Restaurant not found'}</div>;
  }

  // For demo purposes, create mock menu data if none exists
  const menus = restaurant.menus && restaurant.menus.length > 0 
    ? restaurant.menus 
    : [{
        _id: 'mock-menu',
        name: 'Main Menu',
        categories: ['Appetizers', 'Main Course', 'Desserts', 'Beverages'],
        items: [
          {
            _id: 'item1',
            name: 'Margherita Pizza',
            description: 'Classic pizza with tomato sauce, mozzarella, and basil',
            price: 12.99,
            category: 'Main Course',
            image: 'https://via.placeholder.com/150',
            isVegetarian: true,
          },
          {
            _id: 'item2',
            name: 'Caesar Salad',
            description: 'Fresh romaine lettuce with Caesar dressing, croutons, and parmesan',
            price: 8.99,
            category: 'Appetizers',
            image: 'https://via.placeholder.com/150',
          },
          {
            _id: 'item3',
            name: 'Chocolate Cake',
            description: 'Rich chocolate cake with a molten center',
            price: 6.99,
            category: 'Desserts',
            image: 'https://via.placeholder.com/150',
          },
          {
            _id: 'item4',
            name: 'Iced Tea',
            description: 'Refreshing iced tea with lemon',
            price: 2.99,
            category: 'Beverages',
            image: 'https://via.placeholder.com/150',
          },
        ],
      }];

  // Set active category if not set
  if (!activeCategory && menus[0].categories && menus[0].categories.length > 0) {
    setActiveCategory(menus[0].categories[0]);
  }

  return (
    <div className="restaurant-details">
      <div className="restaurant-header">
        <div className="restaurant-cover">
          <img
            src={restaurant.images && restaurant.images.length > 0 
              ? restaurant.images[0] 
              : 'https://via.placeholder.com/1200x400?text=Restaurant'}
            alt={restaurant.name}
          />
        </div>

        <div className="container">
          <div className="restaurant-header-content">
            <h1>{restaurant.name}</h1>

            <div className="restaurant-meta">
              <div className="restaurant-cuisine">
                {restaurant.cuisine && restaurant.cuisine.join(', ')}
              </div>

              <div className="restaurant-rating">
                <div className="stars">{renderStars(restaurant.rating)}</div>
                <span className="rating-count">
                  {restaurant.rating.toFixed(1)} ({restaurant.numReviews} reviews)
                </span>
              </div>

              <div className="restaurant-price">{restaurant.priceRange}</div>
            </div>

            <div className="restaurant-info">
              <div className="info-item">
                <FaMapMarkerAlt />
                <span>
                  {restaurant.address && 
                    `${restaurant.address.street}, ${restaurant.address.city}, ${restaurant.address.state}`}
                </span>
              </div>

              <div className="info-item">
                <FaClock />
                <span>
                  {restaurant.hours && restaurant.hours.monday 
                    ? `${restaurant.hours.monday.open} - ${restaurant.hours.monday.close}`
                    : '9:00 AM - 10:00 PM'}
                </span>
              </div>

              <div className="info-item">
                <FaPhone />
                <span>{restaurant.phone || '(555) 123-4567'}</span>
              </div>

              {restaurant.website && (
                <div className="info-item">
                  <FaGlobe />
                  <a href={restaurant.website} target="_blank" rel="noopener noreferrer">
                    Visit Website
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="restaurant-description">
          <h2>About {restaurant.name}</h2>
          <p>{restaurant.description || 'No description available.'}</p>
        </div>

        <div className="menu-section">
          <h2>Menu</h2>

          <div className="menu-categories">
            {menus[0].categories && menus[0].categories.map((category) => (
              <button
                key={category}
                className={`category-btn ${activeCategory === category ? 'active' : ''}`}
                onClick={() => setActiveCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="menu-items">
            {menus[0].items && menus[0].items
              .filter((item) => item.category === activeCategory)
              .map((item) => (
                <div key={item._id} className="menu-item">
                  <div className="menu-item-image">
                    <img src={item.image || 'https://via.placeholder.com/150'} alt={item.name} />
                  </div>

                  <div className="menu-item-content">
                    <div className="menu-item-header">
                      <h3>{item.name}</h3>
                      <span className="menu-item-price">${item.price.toFixed(2)}</span>
                    </div>

                    <p className="menu-item-description">{item.description}</p>

                    <div className="menu-item-tags">
                      {item.isVegetarian && <span className="tag vegetarian">Vegetarian</span>}
                      {item.isVegan && <span className="tag vegan">Vegan</span>}
                      {item.isGlutenFree && <span className="tag gluten-free">Gluten Free</span>}
                      {item.spicyLevel > 0 && (
                        <span className="tag spicy">
                          Spicy {Array(item.spicyLevel).fill('🌶️').join('')}
                        </span>
                      )}
                    </div>

                    <button 
                      className="btn add-to-cart-btn"
                      onClick={() => handleAddToCart(item)}
                    >
                      Add to Cart
                    </button>
                  </div>
                </div>
              ))}
          </div>
        </div>

        <div className="reviews-section">
          <h2>Customer Reviews</h2>

          {restaurant.reviews && restaurant.reviews.length > 0 ? (
            <div className="reviews-list">
              {restaurant.reviews.map((review) => (
                <div key={review._id} className="review-card">
                  <div className="review-header">
                    <div className="reviewer-info">
                      <h4>{review.user.name}</h4>
                      <div className="review-date">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </div>
                    </div>
                    <div className="review-rating">
                      {renderStars(review.rating)}
                    </div>
                  </div>
                  <p className="review-comment">{review.comment}</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-reviews">No reviews yet. Be the first to review!</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default RestaurantDetails;
