import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { FaCheckCircle, FaHourglass, FaMotorcycle, FaUtensils } from 'react-icons/fa';
import './OrderTracking.css';

const OrderTracking = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentStatus, setCurrentStatus] = useState(0);

  // Mock order statuses
  const orderStatuses = [
    { id: 0, name: 'Order Received', icon: <FaCheckCircle />, time: '5 minutes ago' },
    { id: 1, name: 'Preparing Food', icon: <FaUtensils />, time: 'In progress' },
    { id: 2, name: 'Out for Delivery', icon: <FaMotorcycle />, time: 'Coming soon' },
    { id: 3, name: 'Delivered', icon: <FaCheckCircle />, time: 'Estimated in 25 minutes' }
  ];

  useEffect(() => {
    // Simulate fetching order data
    const fetchOrder = async () => {
      try {
        setLoading(true);

        // In a real app, you would fetch from your API
        // const response = await axios.get(`/api/orders/${orderId}`);
        // setOrder(response.data);

        // Mock data for demonstration
        const mockOrder = {
          _id: orderId,
          restaurant: {
            name: 'Restaurant Name',
            address: '123 Main St, City',
            phone: '(555) 123-4567'
          },
          items: [
            { name: 'Burger', quantity: 2, price: 12.99 },
            { name: 'Fries', quantity: 1, price: 4.99 },
            { name: 'Soda', quantity: 2, price: 2.49 }
          ],
          customer: {
            name: 'John Doe',
            address: '456 Customer St, City',
            phone: '(555) 987-6543'
          },
          total: 35.95,
          status: 'preparing',
          createdAt: new Date().toISOString()
        };

        setOrder(mockOrder);

        // Simulate order progress
        setTimeout(() => setCurrentStatus(1), 3000);
        setTimeout(() => setCurrentStatus(2), 6000);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching order:', error);
        setLoading(false);
      }
    };

    fetchOrder();
  }, [orderId]);

  if (loading) {
    return <div className="loading">Loading order details...</div>;
  }

  if (!order) {
    return <div className="error">Order not found</div>;
  }

  // Calculate order total
  const subtotal = order.items.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const deliveryFee = 2.99;
  const tax = subtotal * 0.08;
  const total = subtotal + deliveryFee + tax;

  return (
    <div className="order-tracking-page">
      <div className="container">
        <div className="order-header">
          <h1>Order Tracking</h1>
          <div className="order-id">Order ID: {order._id}</div>
        </div>

        <div className="tracking-content">
          <div className="tracking-status">
            <h2>Order Status</h2>

            <div className="status-timeline">
              {orderStatuses.map((status, index) => (
                <div 
                  key={status.id} 
                  className={`status-step ${index <= currentStatus ? 'active' : ''}`}
                >
                  <div className="status-icon">{status.icon}</div>
                  <div className="status-info">
                    <div className="status-name">{status.name}</div>
                    <div className="status-time">{status.time}</div>
                  </div>
                  {index < orderStatuses.length - 1 && (
                    <div className={`status-line ${index < currentStatus ? 'active' : ''}`}></div>
                  )}
                </div>
              ))}
            </div>

            <div className="estimated-delivery">
              <h3>Estimated Delivery Time</h3>
              <div className="delivery-time">25-35 minutes</div>
            </div>
          </div>

          <div className="order-details">
            <h2>Order Details</h2>

            <div className="restaurant-info">
              <h3>{order.restaurant.name}</h3>
              <p>{order.restaurant.address}</p>
              <p>Phone: {order.restaurant.phone}</p>
            </div>

            <div className="order-items">
              <h3>Items</h3>
              {order.items.map((item, index) => (
                <div key={index} className="order-item">
                  <div className="item-info">
                    <span className="item-quantity">{item.quantity}x</span>
                    <span className="item-name">{item.name}</span>
                  </div>
                  <span className="item-price">${(item.price * item.quantity).toFixed(2)}</span>
                </div>
              ))}
            </div>

            <div className="price-details">
              <div className="price-row">
                <span>Subtotal</span>
                <span>${subtotal.toFixed(2)}</span>
              </div>
              <div className="price-row">
                <span>Delivery Fee</span>
                <span>${deliveryFee.toFixed(2)}</span>
              </div>
              <div className="price-row">
                <span>Tax</span>
                <span>${tax.toFixed(2)}</span>
              </div>
              <div className="price-row total">
                <span>Total</span>
                <span>${total.toFixed(2)}</span>
              </div>
            </div>

            <div className="delivery-address">
              <h3>Delivery Address</h3>
              <p>{order.customer.name}</p>
              <p>{order.customer.address}</p>
              <p>Phone: {order.customer.phone}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderTracking;
